import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { Context } from '../../contexts';
import Validate from '../../utils/validate';
import Login from './login';

const initialState = {
	name: '',
	password: '',
	email: '',
	hasAgreed: false
};

const initialValidateState = {
	errors: {},
	isLoading: false
};

export default function SignUpForm({ history }) {
	const { stateAccount, handleAddUser } = useContext(Context);
	const [user, setUser] = useState(initialState);
	const [validate, setValidate] = useState(initialValidateState);
	const error = Validate(stateAccount, user, 'sign-up');

	const handleChange = e => {
		const { name, type, checked, value } = e.target;
		setUser({
			...user,
			[name]: type === 'checkbox' ? checked : value
		});
	};

	const handleSubmit = e => {
		e.preventDefault();
		setValidate({
			...validate,
			errors: error,
			isLoading: true
		});
		if (Object.keys(error).length === 0) {
			setTimeout(() => {
				handleAddUser(user);
				localStorage.setItem('notification', 'Successfully Signed In!');
				localStorage.setItem('name', user.name);
				history.push('/main-page');
			}, 1000);
		} else {
			setValidate({
				...validate,
				errors: error,
				isLoading: false
			});
		}
	};

	return (
		<Login>
			<div className="FormCenter">
				<form onSubmit={handleSubmit} className="FormFields">
					<div className="FormField">
						<label className="FormField__Label" htmlFor="name">
							Full Name
						</label>
						<input
							type="text"
							id="name"
							className="FormField__Input"
							placeholder="Enter your full name"
							name="name"
							value={user.name}
							onChange={handleChange}
						/>
						<br />
						<small style={{ color: 'red' }}>{validate.errors.name}</small>
					</div>
					<div className="FormField">
						<label className="FormField__Label" htmlFor="password">
							Password
						</label>
						<input
							type="password"
							id="password"
							className="FormField__Input"
							placeholder="Enter your password"
							name="password"
							value={user.password}
							onChange={handleChange}
						/>
						<br />
						<small style={{ color: 'red' }}>{validate.errors.password}</small>
					</div>
					<div className="FormField">
						<label className="FormField__Label" htmlFor="email">
							E-Mail Address
						</label>
						<input
							type="text"
							id="email"
							className="FormField__Input"
							placeholder="Enter your email"
							name="email"
							value={user.email}
							onChange={handleChange}
						/>
						<br />
						<small style={{ color: 'red' }}>{validate.errors.email}</small>
					</div>

					<div className="FormField">
						<label className="FormField__CheckboxLabel">
							<input
								className="FormField__Checkbox"
								type="checkbox"
								name="hasAgreed"
								value={user.hasAgreed}
								onChange={handleChange}
							/>{' '}
							I agree all statements in{' '}
							<p className="FormField__TermsLink">terms of service</p>
						</label>
					</div>

					<div className="FormField">
						<button
							className="FormField__Button mr-20"
							style={{ marginRight: '20px' }}
						>
							{validate.isLoading ? 'Loading ...' : 'Sign Up'}
						</button>
						<Link to="/sign-in" className="FormField__Link">
							I'm already member
						</Link>
					</div>
				</form>
			</div>
		</Login>
	);
}
