import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { Context } from '../../contexts';
import {
	NotificationContainer,
	NotificationManager
} from 'react-notifications';

import Login from './login';
import Validate from '../../utils/validate';
import { post } from '../../utils/api';

export default function SignInForm({ history }) {
	const {
		stateAccount,
		handleAccount,
		stateSignIn,
		dispatchSignIn,
		signInFunction
	} = useContext(Context);

	// const error = Validate(stateSignIn, 'sign-in');

	// const handleSubmit = e => {
	// 	e.preventDefault();

	// 	setValidate({
	// 		...validate,
	// 		errors: error,
	// 		isLoading: true
	// 	});

	// 	if (Object.keys(error).length === 0) {
	// 		setTimeout(() => {
	// 			post('/login', user)
	// 				.then(res => {
	// 					handleAccount({
	// 						is_authenticated: res.data.accessToken,
	// 						db_profile: user
	// 					});
	// 					localStorage.setItem('notification', 'Successfully Signed In!');
	// 					history.push('/main-page');
	// 				})
	// 				.catch(error => {
	// 					NotificationManager.error(error.response.data);
	// 					setValidate(initialValidateState);
	// 				});
	// 		}, 1000);
	// 	} else {
	// 		setValidate({
	// 			...validate,
	// 			errors: error,
	// 			isLoading: false
	// 		});
	// 	}
	// };

	return (
		<Login>
			<NotificationContainer />
			<div className="FormCenter">
				<form className="FormFields">
					<div className="FormField">
						<label className="FormField__Label" htmlFor="email">
							E-Mail Address
						</label>
						<input
							type="text"
							id="email"
							className="FormField__Input"
							placeholder="Enter your email"
							name="email"
							value={stateSignIn.email}
							onChange={signInFunction}
						/>
						<br />
						<small style={{ color: 'red' }}>{stateSignIn.errors.email}</small>
					</div>

					<div className="FormField">
						<label className="FormField__Label" htmlFor="password">
							Password
						</label>
						<input
							type="password"
							id="password"
							className="FormField__Input"
							placeholder="Enter your password"
							name="password"
							value={stateSignIn.password}
							onChange={signInFunction}
						/>
						<br />
						<small style={{ color: 'red' }}>
							{stateSignIn.errors.password}
						</small>
					</div>

					<div className="FormField">
						<button
							className="FormField__Button mr-20"
							style={{ marginRight: '20px' }}
						>
							{stateSignIn.isLoading ? 'Loading ...' : 'Sign In'}
						</button>{' '}
						<Link to="/" className="FormField__Link">
							Create an account
						</Link>
					</div>
				</form>
			</div>
		</Login>
	);
}
