import * as ACTION_TYPES from '../actions/action_types';

export const initialState = {
	email: '',
	password: '',
	errors: {},
	isLoading: false
};

export const reducer = (state = initialState, action) => {
	switch (action) {
		case 'FIELD': {
			return {
				...state,
				[action.fieldName]: action.payload
			};
		}

		default:
			return state;
	}
};
