import * as ACTION_TYPES from '../actions/action_types';
import * as ACTION from '../actions/action';

export const handleInput = (e, dispatchSignIn) => {
	const { name, value } = e.target;
	dispatchAccount(ACTIONS.FIELD(name, value));
};
