import * as ACTION_TYPES from '../actions/action_types';

export const initialState = {
	email: '',
	password: '',
	errors: [],
	isLoading: false
};

export const reducer = (state = initialState, action) => {
	switch (action.type) {
		case ACTION_TYPES.FIELD: {
			return {
				...state,
				[action.fieldName]: action.payload
			};
		}
		case ACTION_TYPES.VALIDATE: {
			return {
				...state,
				[action.fieldName]: action.payload
			};
		}

		default:
			return state;
	}
};
