import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from '../../context/UserContext';
import {
	NotificationContainer,
	NotificationManager
} from 'react-notifications';
import Validate from '../../tools/Validate';

const initialState = {
	email: '',
	password: ''
};

const initialValidateState = {
	errors: {},
	isLoading: false
};
export default function SignInForm() {
	const { state } = useContext(UserContext);

	const [user, setUser] = useState(initialState);
	const [validate, setValidate] = useState(initialValidateState);
	const error = Validate(state, user, 'sign-in');

	const handleChange = e => {
		const { name, type, checked, value } = e.target;
		setUser({
			...user,
			[name]: type === 'checkbox' ? checked : value
		});
	};

	const handleSubmit = e => {
		e.preventDefault();

		let res = state.find(
			account =>
				account.email === user.email && account.password === user.password
		);
		setValidate({
			...validate,
			errors: error,
			isLoading: true
		});

		if (Object.keys(error).length === 0) {
			setTimeout(() => {
				if (Object.keys(res).length !== 0) {
					NotificationManager.success(`Hello ${res.name}`);
					setUser(initialState);
					setValidate(initialValidateState);
				} else {
					NotificationManager.error(`Invalid email and password`);
				}
			}, 1000);
		} else {
			setValidate({
				...validate,
				errors: error,
				isLoading: false
			});
		}
	};

	return (
		<>
			<NotificationContainer />
			<div className="FormCenter">
				<form onSubmit={handleSubmit} className="FormFields">
					<div className="FormField">
						<label className="FormField__Label" htmlFor="email">
							E-Mail Address
						</label>
						<input
							type="email"
							id="email"
							className="FormField__Input"
							placeholder="Enter your email"
							name="email"
							value={user.email}
							onChange={handleChange}
						/>
					</div>

					<div className="FormField">
						<label className="FormField__Label" htmlFor="password">
							Password
						</label>
						<input
							type="password"
							id="password"
							className="FormField__Input"
							placeholder="Enter your password"
							name="password"
							value={user.password}
							onChange={handleChange}
						/>
					</div>

					<div className="FormField">
						<button
							className="FormField__Button mr-20"
							style={{ marginRight: '20px' }}
						>
							Sign In
						</button>{' '}
						<Link to="/" className="FormField__Link">
							Create an account
						</Link>
					</div>
				</form>
			</div>
		</>
	);
}
