export const initialState = {
	email: '',
	name: '',
	password: '',
	hasAgreed: false,
	errors: [],
	isLoading: false,
	hasLoggedIn: false
};

export const reducer = (state = initialState, action) => {
	switch (action.type) {
		case 'FIELD': {
			return {
				...state,
				[action.fieldName]: action.payload
			};
		}
		case 'VALIDATE': {
			return {
				...state,
				errors: action.payload.errors,
				isLoading: action.payload.isLoading
			};
		}
		case 'RESET': {
			return {
				...state,
				isLoading: true
			};
		}

		default:
			return state;
	}
};
