import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { Context } from '../../contexts';
import {
	NotificationContainer,
	NotificationManager
} from 'react-notifications';
import Validate from '../../utils/validate';
import Login from './login';
import { post } from '../../utils/api';

const initialState = {
	email: '',
	password: ''
};

const initialValidateState = {
	errors: {},
	isLoading: false
};

export default function SignInForm({ history }) {
	const { stateAccount } = useContext(Context);
	const [user, setUser] = useState(initialState);
	const [validate, setValidate] = useState(initialValidateState);
	const error = Validate(stateAccount, user, 'sign-in');

	const handleChange = e => {
		const { name, value } = e.target;
		setUser({
			...user,
			[name]: value
		});
	};

	const handleSubmit = e => {
		e.preventDefault();

		setValidate({
			...validate,
			errors: error,
			isLoading: true
		});

		if (Object.keys(error).length === 0) {
			setTimeout(() => {
				let res = stateAccount.find(
					account =>
						account.email === user.email && account.password === user.password
				);

				if (res) {
					localStorage.setItem('notification', 'Successfully Signed In!');
					localStorage.setItem('name', res.name);
					history.push('/main-page');
				} else {
					NotificationManager.error(`Invalid email and password`);
					setValidate(initialValidateState);
				}
			}, 1000);
		} else {
			setValidate({
				...validate,
				errors: error,
				isLoading: false
			});
		}
	};

	return (
		<Login>
			<NotificationContainer />
			<div className="FormCenter">
				<form onSubmit={handleSubmit} className="FormFields">
					<div className="FormField">
						<label className="FormField__Label" htmlFor="email">
							E-Mail Address
						</label>
						<input
							type="text"
							id="email"
							className="FormField__Input"
							placeholder="Enter your email"
							name="email"
							value={user.email}
							onChange={handleChange}
						/>
						<br />
						<small style={{ color: 'red' }}>{validate.errors.email}</small>
					</div>

					<div className="FormField">
						<label className="FormField__Label" htmlFor="password">
							Password
						</label>
						<input
							type="password"
							id="password"
							className="FormField__Input"
							placeholder="Enter your password"
							name="password"
							value={user.password}
							onChange={handleChange}
						/>
						<br />
						<small style={{ color: 'red' }}>{validate.errors.password}</small>
					</div>

					<div className="FormField">
						<button
							className="FormField__Button mr-20"
							style={{ marginRight: '20px' }}
						>
							{validate.isLoading ? 'Loading ...' : 'Sign In'}
						</button>{' '}
						<Link to="/" className="FormField__Link">
							Create an account
						</Link>
					</div>
				</form>
			</div>
		</Login>
	);
}
