import * as ACTIONS from '../actions/actions';
import { NotificationManager } from 'react-notifications';
import validate from '../../utils/validate';
import { post } from '../../utils/api';

export const handleInput = (e, dispatchSignIn) => {
	const { name, value } = e.target;
	dispatchSignIn(ACTIONS.FIELD(name, value));
};

export const handleSubmit = (e, stateSignIn, dispatchSignIn, history) => {
	e.preventDefault();
	const errors = validate(stateSignIn, 'sign-in');
	dispatchSignIn(
		ACTIONS.VALIDATE({
			errors,
			isLoading: true
		})
	);
	if (Object.keys(errors).length === 0) {
		setTimeout(() => {
			post('/login', stateSignIn)
				.then(res => {
					localStorage.setItem('notification', 'Successfully Signed In!');
					history.push('/main-page');
				})
				.catch(error => {
					NotificationManager.error(error.response.data);
				});
		}, 1000);
	} else {
		dispatchSignIn(
			ACTIONS.VALIDATE({
				errors,
				isLoading: false
			})
		);
	}
};
