import * as ACTIONS from '../actions/actions';
import { NotificationManager } from 'react-notifications';
import validate from '../../utils/validate';
import { post } from '../../utils/api';

export const handleInput = (e, dispatchSignIn) => {
	const { name, value } = e.target;
	dispatchSignIn(ACTIONS.FIELD(name, value));
};

export const handleSubmit = (e, stateSignIn, dispatchSignIn) => {
	e.preventDefault();
	const error = validate(stateSignIn, 'sign-in');
	dispatchSignIn(ACTIONS.VALIDATE(error));
	console.log(error);
	NotificationManager.error('error.response.data');
};
