export default function Validate(value) {
	let error = {};
	if (!value.name) {
		error.name = 'Name is required';
	}

	if (!value.email) {
		error.email = 'Email Address is required';
	} else if (!/\S+@\S+\.\S+/.test(value.email)) {
		error.email = 'Email address is invalid';
	}

	return error;
}
